LL top_arrange(LL mask) {
	if (dp[mask] != -1)return dp[mask];
	if (mask == 0)
		return 1;
	LL ret = 0;
	rep(i, n) {
		if (1&(mask >> i)) {
			rep(j, vec[i].size()) {
				if (1&(mask >> vec[i][j])) {
					goto iafter;
				}
			}
			ret += top_arrange(mask ^ (1 << i));
		}
	iafter:;
	}
	dp[mask] = ret;
	return ret;
}
