#include <iostream>
#include <algorithm>
#include <cmath>
#include <string>
#include <vector>
#include <queue>
typedef unsigned long long ULL;

int main() {

	int h = 1;
	int m = 0;
	int temph = 0;
	int tempm = 0;

	std::string s;

	std::cin >> s;

	for (int i = 0; i < s.size(); ++i) {
		if (s[i] == 'u') {
			if (h == 1) {
				temph += 1;
				if (temph == 24) {
					temph = 0;
				}
			}
			else {
				tempm += 1;
				if (tempm == 60) {
					tempm = 0;
					temph += 1;
				}
				if (temph == 24) {
					temph = 0;
				}
			}
		}
		else if (s[i] == 'd') {
			if (h == 1) {
				temph -= 1;
				if (temph == -1) {
					temph = 23;
				}
			}
			else {
				tempm -= 1;
				if (tempm == -1) {
					tempm = 59;
					temph -= 1;
				}
				if (temph == -1) {
					temph = 23;
				}
			}
		}
		else if (s[i] == 'r') {
			m = 1;
			h = 0;
		}
		else if (s[i] == 'l') {
			m = 0;
			h = 1;
		}
	}
	if (tempm == 60) {
		tempm = 0;
		temph += 1;
	}

	if (temph == 24) {
		temph = 0;
	}

	std::cout << temph << " " << tempm << std::endl;
	return 0;
}
