#include<iostream>
#include<string>
#include<vector>
#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <algorithm>


int main() {
	std::string str;
	std::cin >> str;

	int hour = 0, min = 0;
	bool time = true;
	for(int i=0;i<str.size();i++){
		if (str[i] == 'u') {
			if (time)hour++;
			else min++;
		}
		if (str[i] == 'd') {
			if (time)hour--;
			else min--;

			}
		if (str[i] == 'r') time = false;
		if (str[i] == 'l') time = true;
	}
	while (min >= 60) {
		min -= 60;
		hour++;
	}
	while (min < 0) {
		min += 60;
		hour--;
	}
	while (hour >= 24)hour -= 24;
	while (hour < 0)hour += 24;
	std::cout << hour << " " << min;
	return 0;
}
