#include <iostream>
#include <string>
int main() {
	int jikan = 0;
	int ji = 0, hun = 0;
	std::string str;
	std::cin >> str;
	for (int i = 0; i < str.size(); ++i) {
		if (str[i] == 'u') {
			if (jikan == 0) {
				ji += 1;
			}
			if (jikan == 1) {
				hun += 1;
			}
		}
		if (str[i] == 'd') {
			if (jikan == 0) {
				ji -= 1;
			}
			if (jikan == 1) {
				hun -= 1;
			}
		}
		if (str[i] == 'r')
			jikan = 1;
		if (str[i] == 'l')
			jikan = 0;
	}
	if (ji > 23)
		ji -= 24;
	if (ji < 0)
		ji += 24;
	if (hun > 59)
		hun -= 60;
	if (hun < 0)
		hun += 60;
	std::cout << ji << " " << hun << std::endl;
	system("pause");
	return 0;
}
