#include<iostream>
#include<string>

int main() {
	std::string s;
	std::cin >> s;
	bool flug = true;
	int zi = 0;
	int hun = 0;
	for (int i = 0; i < s.size(); i++) {
		if (s[i] == 'u') {
			if (flug) {
				zi++;
				if (zi == 24) {
					zi = 0;
				}
			}
			else {
				hun++;
				if (hun == 60) {
					hun = 0;
				}
			}
		}
		else if (s[i] == 'd') {
			if (flug) {
				zi--;
				if (zi == -1) {
					zi = 23;
				}
			}
			else {
				hun--;
				if (hun == -1) {
					hun = 59;
				}
			}
		}
		else if (s[i] == 'l') {
			flug = true;
		}
		else {
			flug = false;
		}
	}
	std::cout << zi << ' ' << hun << std::endl;
}
