#include <iostream>
#include <string>
int main() {
	std::string str;
	std::cin >> str;
	//AMAGAERU
	//MGR
	int M = 0, G = 0, R = 0;
	for (int i = 0; i < str.size(); ++i) {
		if (str[i] == 'M')
			M = 1;
		if (str[i] == 'G')
			G = 1; 
		if (str[i] == 'R')
			R = 1;
	}
	if (M == 1 && G == 1 && R == 1) {
		std::cout << "YES" << std::endl;
	}
	else
		std::cout << "NO" << std::endl;
	system("pause");
	return 0;
}
