#include<iostream>
#include<cstring>
#include<algorithm>
#include<vector>
using namespace std;
int main(){
    int n;
    cin>>n;
    vector<int>a(n-1);
    vector<int>b(n-1);
    vector<int>c(n-1);
    for(int i=0;i<n;i++){
        cin>>a[i];
    }
    for(int i=0;i<n;i++){
        if(a[i]%2==1||a[i]==1){
            b[i]=a[i];
        }else{
            c[i]=a[i];
        }
    }
    sort(b.begin(),b.end());
    sort(c.begin(),c.end());
    for(int i=0;i<n;i++){
        if(b[i]==0){}
        else{
        cout<<b[i]<<" ";
        }
    }
    for(int i=0;i<n;i++){
        if(c[i]==0){}
        else{
        cout<<c[i]<<" ";
        }
    }
}
