#include<iostream>
#include<string>

std::string str;

bool m,g,r;

int main(){
    
    std::cin>>str;
    
    for(auto&& x:str){
        if(x=='m')
            m=true;
        if(x=='g')
            g=true;
        if(x=='r')
            r=true;
        if(m&&g&&r)
            break;
    }
    
    if(m&&g&&r)
        std::cout<<"YES"<<std::endl;
    
    else
        std::cout<<"NO"<<std::endl;
    
}
