#include "iostream"
#include "string"
#include "algorithm"
#include "vector"

using namespace std;

int main() {
	string s, amagaeru = "AMAGAERU";
	cin >> s;
	bool flag = true;
	int to = -1, tt = -1, th = -1;

	if (s.size() > amagaeru.size()) {
		cout << "NO" << endl;
		return 0;
	}


	for (int i = 0; i <= 1; i++) {
		if (i > s.size()) break;
		if (s[i] == 'M') to = i;
		else if (s[i] != 'A') flag = false;
	}
	if (to == -1) {
		flag = false;
	}

	for (int i = to + 1; i <= to + 2; i++) {
		if (i > s.size()) break;
		if (s[i] == 'G') tt = i;
		else if (s[i] != 'A') flag = false;
	}
	if (to == tt) {
		flag = false;
	}

	for (int i = tt + 1; i <= tt + 3; i++) {
		if (i > s.size()) break;
		if (s[i] == 'R') th = i;
		else if (s[i] != 'A' && s[i] != 'E') flag = false;
	}
	if (th == tt) {
		flag = false;;
	}

	if (flag) cout << "YES" << endl;
	else cout << "NO" << endl;

	return 0;
}
