#define ll long long
#define rep(i,rept) for (ll i = 0; i < rept; i++)
#define Yes(b) std::cout<< (b ? "Yes" : "No")<<std::endl
#define YES(b) std::cout<< (b ? "YES" : "NO")<<std::endl
#define fr first
#define se second
using namespace std;
int vowel(string s) {
	int n = 0;
	while (n < s.size()) {
		if (s[n] == 'a' || s[n] == 'b' || s[n] == 'c' || s[n] == 'd' || s[n] == 'e') {
			n++;
		}
		else break;
	}
	return n;
}
map<string, bool> m = {};
map<string, int> kasira = {};
int main() {
	string s, str;
	int n, r = 0, l;
	cin >> n;
	rep(i, n) {
		cin >> r >> s;
		if (r == 0) {
			m[s] = true;
			str = {};
			l = vowel(s);
			if (l >= 0) {
				rep(i, l) {
					str += s[i];
					kasira[str]++;
				}
			}
		}
		if (r == 1) {
			Yes(m[s]);
		}
		if (r == 2) {
			cout << kasira[s] << endl;
		}
	}
	return 0;
}
