#include<iostream>

int main() {
	int N;
	std::cin >> N;
	if (N % 15 == 0) {
		std::cout << "FizzBuzz" << std::endl;
	}
	else if (N % 5 == 0) {
		std::cout << "Buzz" << std::endl;
	}
	else if (N % 3 == 0) {
		std::cout << "Fizz" << std::endl;
	}
	else {
		std::cout << N << std::endl;
	}
	return 0;
}
