#include <iostream>
#include <string>
#include <fstream>

const std::string s = "AMAGAERU";
const std::string m = "AIUEO";

int main() {
    std::string str;
    
    std::cin >> str;
    
    int a = 0;
    for(int i = 0; i < s.size(); ++i) {
        if(str[a] == s[i]) {
            ++a;
        }else if (m.find(s[i]) == std::string::npos) {
            std::cout << "NO" << std::endl;
            
            return 0;
        }
    }
    
    std::cout << "YES" << std::endl;
}
