#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>
#include <map>
#include <queue>
#include <stack>
#define rep(i,n) for(int i=0;i<n;++i)

using namespace std;
int a, m, g, e, r;
string s;
int main() {
	cin >> s;
	rep(i, s.size()) {
		if (s[i] == 'A') a++;
		else if (s[i] == 'E') e++;
		else if (s[i] == 'M') {
			if (a > 1) break;
			m++;
		}
		else if (s[i] == 'G') {
			if (a > 2 || m != 1) break;
			g++;

		}
		else if (s[i] == 'R') {
			if (a > 3 || m != 1 || g != 1) break;
			r++;
		}
		else break;
	}
	if (r == 1) cout << "YES" << endl;
	else cout << "NO" << endl;
	return 0;
}
