#include <iostream>
#include <string>
#include<cstdlib>
#include<algorithm>
#include<string>
#include<vector>
using namespace std;

void sortsan(vector<int>nanika) {
	for (int s = 0; s < nanika.size() - 1; s++) {
		for (int t = s + 1; t < nanika.size(); t++) {
			if (nanika[t] > nanika[s]) {
				int tmp = nanika[t];
				nanika[t] = nanika[s];
				nanika[s] = tmp;
			}
		}
	}
}

int main() {
	vector<int>kazu;
	int N;
	int kasu;
	vector<int>kisuu;
	vector<int>guusuu;
	cin >> N;
	for (int i = 0; i < N; i++) {
		cin >> kasu;
		kazu.push_back(kasu);
		if (kazu[i] % 2 == 1) {
			kisuu.push_back(kazu[i]);
		}
		else {
			guusuu.push_back(kazu[i]);
		}
	}
	sortsan(kisuu);
	sortsan(guusuu);
	for (int i = 0; i < kisuu.size(); i++) {
		cout << kisuu[i];
	}
	for (int i = 0; i < guusuu.size(); i++) {
		cout << guusuu[i];
	}
}
