#include<iostream>
#include<cstring>
#include<algorithm>
#include<vector>
using namespace std;
int main(){
    int n;
    cin>>n;
    vector<int>a(n);
    vector<int>b(n);
    vector<int>c(n);
    vector<int>d(n);
    for(int i=0;i<n;i++){
        cin>>a[i];
    }
    for(int i=0;i<n;i++){
        if(a[i]==1){
            a[i]=d[i];
        }
        else if(a[i]%2==1){
            b[i]=a[i];
        }else{
            c[i]=a[i];
        }
    }
    sort(b.begin(),b.end());
    sort(c.begin(),c.end());
    for(int i=0;i<n;i++){
        if(d[i]==1){
        cout<<d[i]<<" ";
        }
    }
    for(int i=0;i<n;i++){
        if(b[i]==0){}
        else{
        cout<<b[i]<<" ";
        }
    }
    for(int i=0;i<n;i++){
        if(c[i]==0){}
        else{
        cout<<c[i]<<" ";
        }
    }
    cout<<endl;
}
