#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>
#include <math.h>
int left(int h, int w, int x)
{
	if(w == 1) return x;
	if(x % w == 1){
		return x - 1 + w;
	}
	return x - 1;
}
int right(int h, int w, int x)
{
	if(w == 1) return x;
	if(x % w == w){
		return x + 1 - w;
	}
	return x + 1;
}
int up(int h, int w, int x)
{
	if(h == 1) return x;
	if((x) % w == 0){
		return x + w * (h - 1);
	}
	return x - w;
}
int down(int h, int w, int x)
{
	if(h == 1) return x;
	if((x) % w == h - 1){
		return x - w * (h - 1);
	}
	return x + w;
}
int main()
{
	int h, w, x;
	std::vector<int> vec;
	std::cin >> h >> w >> x;
	vec.push_back(left(h,w,up(h,w,x)));
	vec.push_back(up(h,w,x));
	vec.push_back(right(h,w,up(h,w,x)));
	vec.push_back(left(h,w,x));
	vec.push_back(right(h,w,x));
	vec.push_back(left(h,w,down(h,w,x)));
	vec.push_back(down(h,w,x));
	vec.push_back(right(h,w,down(h,w,x)));
	std::sort(vec.begin(),vec.end());
	for(int i = 0; i < vec.size(); i++){
		std::cout << vec[i];
		if(i != vec.size() - 1) std::cout << " ";
	}
	std::cout << std::endl;
	return 0;
}
