#include "bits/stdc++.h"

using namespace std;
using ll = long long;
using ull = unsigned long long;

typedef pair<ll, ll> P;
typedef pair<double, double >pd;
typedef pair<string, string> sP;
typedef pair<ll, P> PP;
typedef pair<P, P> PPP;

const ll MOD = 1e9 + 7;
const ll MOD2 = 998244353;
const ll INF = 1 << 30;
const ll INF2 = 9e18;
const double INF3 = 5e14;
const double eps = 1e-10;
const double PI = acos(-1);
const int dx[4] = { 1,0,-1,0 }, dy[4] = { 0,1,0,-1 };

#define ALL(x) (x).begin(),(x).end()
#define ALLR(x) (x).rbegin(),(x).rend()
#define pb push_back
#define eb emplace_back
#define fr first
#define sc second


ll n, m, a, b, dp[1 << 16];
vector<ll>v[20];

int main() {
	cin >> n >> m;
	for (int i = 0;i < m;i++) {
		cin >> a >> b;
		v[a].eb(b);
	}
	dp[0] = 1;
	for (int i = 0;i < (1 << n);i++) {
		for (int j = 0;j < n;j++) {
			if (~i&(1 << j)) {
				bool ok = true;
				for (int k : v[j]) {
					if (i&(1 << k)) {
						ok = false;
					}
				}
				if (ok) {
					dp[i | 1 << j] += dp[i];
				}
			}
		}
	}
	cout << dp[(1 << n) - 1] << endl;
	return 0;
}
