#include<iostream>
#include<vector>
#include <algorithm>

std::vector<int> vec;

int n;

int aaa;

int main(){
    
    int a;
    
    std::cin>>n;
    
    for(int i=0;i<n;i++){
        std::cin>>a;
        vec.push_back(a);
    }
    
    std::sort(vec.begin(),vec.end(),[](int& left,int& right){
        return left%2==right%2 ? left<right : left%2;
    });
    
    for(auto&& x:vec){
        std::cout<<x;
        aaa++;
        if(aaa!=n)
            std::cout<<' ';
    }
    
    std::cout<<std::endl;
    
    return 0;
    
}
