#include <iostream>
#include <string>
using namespace std;

int f(string s, char c)
{
	int ans = 0;
	for (auto x : s)
		if (x == c) ans++;
	return ans;
}

int main()
{
	string s;
	cin >> s;
	cout << (s.size() == 7 && f(s, 'a') == 2 && f(s, 'c') == 1 && f(s, 'h') == 1 && f(s, 'n') == 2 && f(s, 't') == 1 ? "Yes" : "No") << endl;
}
