#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

int main() {

    std::string in;

    std::cin>>in;

    std::vector<std::string> vec;

    std::string tmp;

    for(int i=0;i<in.size();++i){
        if(in[i]==':'){
            vec.emplace_back(tmp);
            tmp="";
        }
        else
            tmp+=in[i];
    }

    vec.emplace_back(tmp);

    int max=0,maxv=0,cnt=0;

    for(int i=vec.size()-1;i>=0;--i){
        if(vec[i]=="0000")
            ++cnt;
        else{
            if(max<=cnt){
                maxv=i+1;
                max=cnt;
            }
            cnt=0;
        }
    }

    if(max==0)
        maxv=-1;

    for(int i=0;i<vec.size();++i){
        if(i==maxv)
            std::cout<<":";
        else if(i<maxv||maxv+max<=i){
            std::cout<<vec[i];
            if(i!=vec.size()-1)
                std::cout<<':';
        }
    }

    std::cout<<std::endl;

    return 0;
}
