#include<iostream>
#include<vector>
int main() {
	int n;
	std::vector<int> a,b;
	std::cin >> n;
	for (int i = 0; i < n; i++) {
		int x;
		std::cin >> x;
		if (x % 2 == 1) {
			a.push_back(x);
			for (int j = 0; j < a.size() - 1; j++) {
				if (a[i - j] < a[i - j - 1]) {
					x = a[i - j];
					a[i - j] = a[i - j - 1];
					a[i - j - 1] = x;
				}
				else {
					break;
				}
			}
		}
		else {
			b.push_back(x);
			if (b.size() > 2) {
				for (int j = 0; j < b.size() - 1; j++) {
					if (b[b.size()-1 - j] < b[b.size()-1 - j - 1]) {
						x = b[b.size()-1 - j];
						b[b.size()-1- j] = b[b.size()-1 - j - 1];
						b[b.size()-1 - j - 1] = x;
					}
					else {
						break;
					}
				}
			}
		}
	}
	for (int i = 0; i < a.size(); i++) {
		std::cout << a[i] << ' ';
	}
	for (int i = 0; i < b.size(); i++) {
		std::cout << b[i] << ' ';
	}
std::cout << std::endl;
}
