#include<iostream>
#include<string>

std::string str;

int tttt;

enum tmtmdsdds{hour,minute};

tmtmdsdds selectee=hour;

int main(){
    
    std::cin>>str;
    
    for(auto&& x:str){
        if(x=='u'){
            if(selectee==hour)
                tttt+=60;
            else
                tttt++;
        }
        if(x=='d'){
            if(selectee==hour)
                tttt-=60;
            else
                tttt--;
        }
        if(x=='r')
            selectee=minute;
        if(x=='l')
            selectee=hour;
    }
    
    tttt=(tttt+1440*10000)%1440;
    
    std::cout<<tttt/60<<' '<<tttt%60<<std::endl;
    
    return 0;
    
}
