#include <iostream>
#include <vector>
#include <string>
#include <algorithm>
int main(){
    int check=0,hour=0,minute=0;
    std::string s;
    std::cin>>s;
    for(int i=0;i<s.size();++i){
        if(s[i]=='u'){
            if(check==0){
                ++hour;
            }
            if(check==1){
                ++minute;
            }
        }
        if(s[i]=='d'){
            if(check==0){
                --hour;
            }
            if(check==1){
                --minute;
            }
        }
        if(s[i]=='r'){
            check=1;
        }
        if(s[i]=='l'){
            check=0;
        }
    }
    if(hour>=24){
        hour=hour%24;
    }
    if(hour<0){
        hour=-hour;
        hour=24-hour%24;
    }
    if(minute>=60){
        minute=minute%60;
    }
    if(minute<0){
        minute=-minute;
        minute=60-minute%60;
    }
    std::cout<<hour<<" "<<minute<<std::endl;
}
