#include <string>
#include <algorithm>
#include <iostream>
using namespace std;


int main() {
	string str;
	cin >> str;
	int s = str.size();
	int h = 0; 
	int m = 0;
	int w = 1;
	for (int i = 0; i < s; i++) {

		if (str[i] == 'r') {
			w = 2;
		}
		else if (str[i] == 'l') {
			w = 1;
		}
		//w=1のとき時間選択中 w=2のとき分選択中

		//時間選択中
		if (w == 1) {
			if (str[i] == 'u') {
				h++;
				if (h == 24) {
					h = 0;
				}
			}
			else if(str[i]=='d'){
				if (h == 0) {
					h = 24;
				}
				h--;
			}
		}

		//分選択中
		if (w == 2) {
			if (str[i] == 'u') {
				m++;
				if (m == 60) {
					m = 0;
				}
			}
			else if (str[i] == 'd') {
				if (m == 0) {
					m = 60;
				}
				m--;
			}
		}

	}


	cout << h << " " << m << endl;



}
