#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>


int main() {
	int h = 0, m = 0;
	std::string s;
	std::cin >> s;
	int check = 0;
	//偶数時　奇数分
	for (int i = 0; i < s.size(); i++) {
		if (s[i] == 'r' || s[i] == 'l') {
			check++;
		}

		else if (s[i] == 'u') {
			if (check % 2 == 0) {
				h++;
			}
			else {
				m++;
			}
		}

		else {
			if (check % 2 == 0) {
				h--;
			}
			else {
				m--;
			}
		}
	}

	if (h < 0) {
		std::cout << (h + (std::abs(h) / 24 + 1)*24) % 24 << " ";
	}
	else {
		std::cout << h % 24 << " ";
	}
	if (m < 0) {
		std::cout << (m + (std::abs(m) / 60 + 1)*60) % 60 << std::endl;
	}
	else {
		std::cout << m % 60 << std::endl;
	}
}
