#include<iostream>
#include<cstring>
using namespace std;
int main(){
    string a;
    cin>> a;
    for(int i=0;i<a.length();i++){
        if(a[i]<97){
            a[i]=a[i]+32;
        }else if(a[i]>=65){
            a[i]=a[i]-32;
        }
    }
    for(int i=0;i<a.length();i++){
        cout<<a[i];
    }
    cout<<endl;
    return 0;
}
