#include<iostream>
#include<string>

std::string str;

int hourt,minutet;

enum tmtmdsdds{hour,minute};

tmtmdsdds selectee=hour;

int main(){
    
    std::cin>>str;
    
    for(auto&& x:str){
        if(x=='u'){
            if(selectee==hour)
                hourt++;
            else
                minutet++;
        }
        if(x=='d'){
            if(selectee==hour)
                hourt--;
            else
                minutet--;
        }
        if(x=='r')
            selectee=minute;
        if(x=='l')
            selectee=hour;
    }
    
    hourt=(hourt+24*10000)%24;
    
    minutet=(minutet+60*10000)%60;
    
    std::cout<<hourt<<' '<<minutet<<std::endl;
    
    return 0;
    
}
