#include<iostream>
#include<vector>
#include<algorithm>
#include<functional>
#include<map>
#include<string.h>
#include<string>
#include<cmath>
#include<utility>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
typedef long long ll;

int main() {
	int n = 0, a[100] = {};
	int kisu[100] = {};
	int gusu[100] = {};
	int count_kisu = 0, count_gusu = 0;
	std::cin >> n;
	rep(i, n) {
		std::cin >> a[i];
		if (a[i] % 2 == 0) {
			gusu[count_gusu] = a[i];
			count_gusu++;
		}
		else {
			kisu[count_kisu] = a[i];
			count_kisu++;
		}
	}
	std::sort(gusu, gusu + count_gusu);
	std::sort(kisu, kisu + count_kisu);
	
	for (int i = 0; i < count_kisu; i++) {
		std::cout << kisu[i] << " ";
	}
	for (int i = 0; i < count_gusu; i++) {
		std::cout << gusu[i] << " ";
	}
	std::cout << std::endl;
}
