#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <cmath>
using namespace std;
int main() {
	string s;
	cin >> s;
	int Y, X, Z;
	Y = s.find('M', 0);
	X = s.find('G', 0);
	Z = s.find('R', 0);
	if (Y < X&&X < Z) {
		cout << "YES" << endl;
	}
	else {
		cout << "NO" << endl;
	}
}
