#include <iostream>
#include <vector>
void fanc(std::vector <int>& vec) {
	for (int j = 0; j < 50; ++j) {
		for (int i = 0; i < vec.size() - 1; ++i) {
			int m;
			if (vec[i] > vec[i + 1]) {
				m = vec[i];
				vec[i] = vec[i + 1];
				vec[i + 1] = m;
			}
		}
	}
}
int main() {
	int N;
	std::cin >> N;
	std::vector <int> vec;
	std::vector <int> vec2;
	std::vector <int> vec3;
	for (int i = 0; i < N; ++i) {
		int x;
		std::cin >> x;
		vec.push_back(x);
	}
	for (int i = 0; i < vec.size(); ++i) {
		if (vec[i] % 2 == 1)
			vec2.push_back(vec[i]);
	}
	for (int i = 0; i < vec.size(); ++i) {
		if (vec[i] % 2 == 0)
			vec3.push_back(vec[i]);
	}
	fanc(vec2), fanc(vec3);
	for (int i = 0; i < vec2.size(); ++i) {
		std::cout << vec2[i] << " ";
	}
	for (int i = 0; i < vec3.size(); ++i) {
		std::cout << vec3[i] << " ";
	}
	std::cout << std::endl;
	system("pause");
	return 0;
}
