#include <iostream>
#include <string>
int main(){
    std::string s;
    std::cin>>s;
    for(int i=0;i<s.size();++i){
        if('A'<=s[i]&&s[i]<='Z'){
            s[i]=s[i]+32;
        }
        if('a'<=s[i]&&s[i]<='z'){
            s[i]=s[i]-32;
        }
    }
    std::cout<<s<<std::endl;
}
