#include<iostream>
#include<string>
int main() {
	std::string s;
	std::cin >> s;
	int check;
	for (int i = 0; i < s.size(); i++) {
		if (s[i] == 'M') {
			check = 1;
		}
		if (s[i] == 'G'&&check == 1) {
			check = 2;
		}
		if (s[i] == 'R'&&check == 2) {
			check = 3;
		}
	}
	if (check == 3) {
		std::cout << "YES" << std::endl;
	}
	else {
		std::cout << "No" << std::endl;
	}
}
