#include <iostream>
#include <string>

int main() {
	std::string a;
	std::cin >> a;
	int aa = 0;
	for (int i = 7; i < int(a.size())+7; i+=7) {
		char d1=a[i] ,d2=a[i+1],d3 = a[i+2],d4 = a[i+3],d5 = a[i+4],d6 = a[i+5],d7 = a[i+6];
		if (d1 == 'M' or d2 == 'M' or d3 == 'M' or d4 == 'M' or d5 == 'M' or d6 == 'M' or d7 == 'M') {
			if (d1 == 'G' or d2 == 'G' or d3 == 'G' or d4 == 'G' or d5 == 'G' or d6 == 'G' or d7 == 'G') {
				if (d1 == 'R' or d2 == 'R' or d3 == 'R' or d4 == 'R' or d5 == 'R' or d6 == 'R' or d7 == 'R') {
					aa++;
				}
			}
		}
	}
	if (aa == 0) {
		std::cout << "NO" << std::endl;
	}
	else {
		std::cout << "YES" << std::endl;
	}
}
