#include<iostream>
#include<vector>
int main() {
	int n; 
	std::cin >> n;
	std::vector<int> x;
	for (int i = 0; i < n; i++){
		int d;
	std::cin >> d;
	x.push_back(d);
}
	for (int i = 0; i < n ; i++) {
		for (int s = i+1; s < n; s++) {
			if (x[s] < x[i]) {
				int a = x[s];
				x[s] = x[i];
				x[i] = a;
			}
		}
	}
	for (int f = 0; f < n ; f++) {
		for (int t =f+ 1; t < n; t++) {
			if (x[f] % 2 == 0 && x[t] % 2 == 1) {
				int b = x[t];
				x[t] = x[f];
				x[f] = b;
			}
		}
		std::cout << x[f];
	}
