#include <iostream>
#include <string>
int main(){
    std::string s;
    std::cin>>s;
    for(int i=0;i<s.size();++i){
        if(97<=s[i]&&s[i]<=122){
            s[i]=s[i]-32;
        }
        else if(65<=s[i]&&s[i]<=90){
            s[i]=s[i]+32;
        }
    }
    std::cout<<s<<std::endl;
}
