#include<iostream>
#include<string>
int main()
{
	std::string N;
	for (int i = 0; i >= 2000000; i++) {
		if(N[i] % 3 = 0){
		if (N[1] = 0) {
			std::cout << "FizzBuzz" << std::endl;
		}
		else if (N[1] = 5) {
			std::cout << "FizzBuzz" << std::endl;
		}
	}
	else if (N[1] = 0) {
		if (N[1] = 5) {
			std::cout << "Buzz" << std::endl;
		}
	}
	else if (N % 3 = 0) {
		std::cout << "Fizz" << std::endl;
	}
	else {
		std::stoi(N);
	}
	return 0;
}
