#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>
#include <math.h>
#include <stdlib.h>

int main()
{
	std::string a, b;
	int vec[51] = {}, ai[50]={}, bi[50]={};
	std::cin >> a >> b;
	for(int i = 0; i < a.size(); i++){
		ai[i] = a[a.size() - 1 - i] - '0';
	}
	for(int i = 0; i < b.size(); i++){
		bi[i] = b[b.size() - 1 - i] - '0';
	}
	for(int i = 0; i < 50; i++){
		vec[i] += ai[i] + bi[i];
		vec[i + 1] += vec[i] / 10;
		vec[i] %= 10;
	}
	bool zero = true;
	for(int i = 50; i > -1; i--){
		if(vec[i] != 0 && zero){
			zero = false;
		}
		if(!zero){
			std::cout << vec[i];
		}
	}
	 std::cout << std::endl;
	return 0;
}
