#include<iostream>
#include<cstring>
using namespace std;
int main(){
    string a;
    cin>> a;
    int sum=0;
    for(int i=0;i<a.length();i++){
        if(a[i]=='M'){
            sum+=1;
        }
    }
    for(int i=0;i<a.length();i++){
        if(a[i]=='G'){
            sum+=1;
        }
    }
    for(int i=0;i<a.length();i++){
        if(a[i]=='R'){
            sum+=1;
        }
    }
    if(sum==3){
        cout<<"YES"<<endl;
    }else{
        cout<<"NO"<<endl;
    }
}
