#include <string>
#include <algorithm>
#include <iostream>
using namespace std;


int main() {
	string str;
	cin >> str;
	int r, m, g;
	r = 0;
	m = 0;
	g = 0;
	int s = str.size();
	for (int i = 0; i < s; i++) {
		if (str[i] == 'R') {
			r++;
		}
		if (str[i] == 'M') {
			m++;
		}
		if (str[i] == 'G') {
			g++;
		}
}

	if (r != 0 && m != 0 && g != 0) {
		cout << "YES" << endl;
	}
	else {
		cout << "NO" << endl;
	}
}
