#include <algorithm>
#include <iostream>
#include <string>
#define MAX 100

int main() {
	int n;
	int k[MAX], g[MAX];
	std::cin >> n;
	for (int i = 0; i < n; i++) {
		int gg;
		std::cin >> gg;
		if (gg % 2 == 0) {
			g[i] = gg;
		}
		else if (gg % 2 == 1) {
			k[i] = gg;
		}
	}
	for (int i = 0; i<100; ++i) {
		for (int j = i + 1; j<100; ++j) {
			if (g[i] > g[j]) {
				int tmp = g[i];
				g[i] = g[j];
				g[j] = tmp;
			}
		}
	}
	for (int i = 0; i<100; ++i) {
		for (int j = i + 1; j<100; ++j) {
			if (k[i] > k[j]) {
				int tmp = k[i];
				k[i] = k[j];
				k[j] = tmp;
			}
		}
	}
	for (int i = 0; i < 100; ++i) {
		std::cout << k[i] << ' ';
	}
	for (int i = 0; i < 100; ++i) {
		std::cout << g[i] << ' ';
	}
	std::cout << std::endl;
}
