#include <iostream>
#include <string>
#include<cstdlib>
#include<algorithm>
#include<string>
#include<vector>
using namespace std;

int main() {
	string S;
	cin >> S;
	for (int i = 0; i < S.length(); i++) {
		if (S[i] >= 'A' || S[i] <= 'Z') {
			S[i] = S[i] - ('A' - 'a');
		}
		else if (S[i] >= 'a' || S[i] <= 'z') {
			S[i] = S[i] - ('a' - 'A');
		}
		cout << S[i];
	}
	cout << '\n';
}
