#include <iostream>
#include <string>
int main(){
	int w=0;
	std::string str;
	std::cin>>str;
	for(int i=0;i<str.size();i++){
		if(str[i]=='R'){
			for(int j=0;j<str.size();j++){
				if(str[j]=='M'){
					for(int k=0;k<str.size();k++){
						if(str[k]=='G'){
							w=1;
						}
					}
				}
			}
		}
	}
	if(w==1){
		std::cout<<"YES"<<std::endl;
	}else{
		std::cout<<"NO"<<std::endl;
	}
	return 0;
}
