#include<iostream>
#include<vector>
#include<algorithm>
#include<functional>
#include<map>
#include<string.h>
#include<string>
#include<cmath>
#include<utility>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
typedef long long ll;

int main() {
	int zi = 0, fun = 0;
	bool which = true;//true 時間　false 分
	std::string str;
	std::cin >> str;
	rep(i, str.length()) {
		if (str[i] == 'u') {
			if (which) {
				zi++;
				if (zi >= 24) {
					zi = zi - 24;
				}
			}
			else {
				fun++;
				if (fun >= 60) {
					fun = fun - 60;
				}
			}
		}
		if (str[i] == 'd') {
			if (which) {
				zi--;
				if (zi == -1) {
					zi = 23;
 				}
			}
			else {
				fun--;
				if (fun == -1) {
					fun = 59;
				}
			}
		}
		if (str[i] == 'r') {
			which = false;
		}
		if (str[i] == 'l') {
			which = true;
		}
	}
	if (fun < 10) {
		std::cout << zi << " " << fun << std::endl;
	}
	else {
		std::cout << zi << " " << fun << std::endl;
	}
	return 0;
}
