#include <iostream>
#include <string>
#include <algorithm>
#include <vector>
#include <cmath>
#include <queue>
#include <stack>
#define rep(i,n) for(int i=0;i<n;++i)
#define FOR(i,s) for(int i=1;i<s;++i)

using ll = long long;
using namespace std;

int h, w, x, add = 1, A[10101][10101], B[8], px, py;
int main() {
	cin >> h >> w >> x;
	FOR(i, h + 1) FOR(j, w + 1) {
		A[i][j] = add;
		if (x == add) {
			px = i;
			py = j;
		}
		add++;
	}
	rep(i, w + 2) {
		if (i != 0 && i != w + 1) A[0][i] = A[w][i];
		else if (i == 0) A[0][i] = A[h][w];
		else A[0][i] = A[h][1];
	}
	rep(i, h + 2) {
		if (i != 0 && i != h + 1) A[i][0] = A[i][h];
		else if (i == h + 1) A[i][0] = A[1][h];
	}
	rep(i, w + 2) {
		if (i != 0 && i != w + 1) A[w + 1][i] = A[1][i];
		else if (i == w + 1)A[h + 1][w + 1] = A[1][1];
	}
	rep(i, h + 2) {
		if (i != 0 && i != h + 1) A[i][h + 1] = A[i][1];
	}
	B[0] = A[px - 1][py - 1];
	B[1] = A[px - 1][py];
	B[2] = A[px - 1][py + 1];
	B[3] = A[px][py - 1];
	B[4] = A[px][py + 1];
	B[5] = A[px + 1][py - 1];
	B[6] = A[px + 1][py];
	B[7] = A[px + 1][py + 1];
	sort(B, B + 8);
	rep(i, 8) {
		if (i != 7)
			cout << B[i] << " ";
		else
			cout << B[i];
	}
	cout << endl;
	return 0;
}
