#include <iostream>
#include <algorithm>
#include <string>
#include <vector>
#include <map>
#include <iomanip>
#include <cmath>
#include <cstdlib>
#include <math.h>
#include <stdlib.h>

int main()
{
	std::string str;
	std::getline(std::cin,str);
	bool M=false, G=false, R=false;
	for(int i = 0; i < str.size(); i++){
		if(str[i] == 'M') M = true;
		if(str[i] == 'G') G = true;
		if(str[i] == 'R') R = true;
	}
	if(M&&G&&R) std::cout << "Yes" << std::endl;
	else std::cout << "No" << std::endl;
	return 0;
}
