#include<iostream>
#include<string>
#include<cmath>
#include<algorithm>

unsigned long long a,b;

bool am,bm;

std::string aa,bb;

int main(){
    
    std::cin>>aa>>bb;
    
    if(aa[0]=='-'){
        am=1;
        std::reverse(aa.begin(), aa.end());
        for(int i=0;i<aa.size()-1;i++)
            a+=std::pow(10,i)*(aa[i]-'0');
    }
    else{
        std::reverse(aa.begin(), aa.end());
        for(int i=0;i<aa.size();i++)
            a+=std::pow(10,i)*(aa[i]-'0');
    }
    
    if(bb[0]=='-'){
        bm=1;
        std::reverse(bb.begin(), bb.end());
        for(int i=0;i<bb.size()-1;i++)
            b+=std::pow(10,i)*(bb[i]-'0');
    }
    else{
        std::reverse(bb.begin(), bb.end());
        for(int i=0;i<bb.size();i++)
            b+=std::pow(10,i)*(bb[i]-'0');
    }
    
    if(am==1){
        if(bm==1)
            std::cout<<-a-b<<std::endl;
        else
            std::cout<<-a+b<<std::endl;
    }
    else{
        if(bm==1)
            std::cout<<a-b<<std::endl;
        else
            std::cout<<a+b<<std::endl;
    }
    
    return 0;
    
}
