#define ll long long int
#define rep(i,rept) for (ll i = 0; i < rept; i++)
#define Yes(b) std::cout<< (b ? "Yes" : "No")<<std::endl
#define YES(b) std::cout<< (b ? "YES" : "NO")<<std::endl
#define fr first
#define se second
#define MAX_N 2000
#define MAX_B 20
#define se second
#define lolo 92233720368547758
using namespace std;
ll dp[MAX_N + 1][MAX_B*MAX_N + 1];
ll W;
int n, a[MAX_N + 1], b[MAX_N + 1 ];

void solve() {
	fill(dp[0], dp[0] + MAX_B * MAX_N + 1, lolo);
	dp[0][0] = 0;
	for (ll i = 0; i < n; i++) {
		for (ll j = 0; j <= MAX_B * MAX_N; j++) {
			if (j < b[i]) {
				dp[i + 1][j] = dp[i][j];
			}
			else {
				dp[i + 1][j] = min(dp[i][j], dp[i][j - b[i]] + a[i]);
			}
		}
	}
	int res = 0;
	for (ll i = 0; i <= MAX_B * MAX_N; i++) {
		if (dp[n][i] <= W)res = i;
	}
	cout << res << endl;
}

int main() {
	cin >> n >> W;
	rep(i, n) {
		cin >> a[i] >> b[i];
	}
	solve();
	return 0;
}
