#include <iostream>
#include <string>
int main(){
	int h=0,m=0,w=0;
	std::string str;
	std::cin>>str;
	for(int i=0;i<str.size();i++){
		if(str[i]=='r'){
			w=1;
		}
		if(str[i]=='l'){
			w=0;
		}
	}
	if(w==1){
		for(int i=0;i<str.size();i++){
			if(str[i]=='u'){
				m++;
			}
			if(str[i]=='d'){
				m--;
			}
			if(m>59){
				m-=60;
			}
			if(m<0){
				m+=60;
			}
		}
	}
	if(w==0){
		for(int i=0;i<str.size();i++){
			if(str[i]=='u'){
				h++;
			}
			if(str[i]=='d'){
				h--;
			}
			if(h>=24){
				h-=24;
			}
			if(h<0){
				h+=24;
			}
		}
	}
	std::cout<<h<<" "<<m<<std::endl;
	return 0;
}
