#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
int main(){
    int h,w,x;
    std::cin>>h>>w>>x;
    std::vector<int> v(h*w-1);
    v[1]=x-w;
    v[0]=v[1]-1;
    v[2]=v[1]+1;
    v[3]=x-1;
    v[4]=x+1;
    v[6]=x+w;
    v[5]=v[6]-1;
    v[7]=v[6]+1;
    for(int i=0;i<h*w-1;++i){
        if(v[i]>h*w){
            v[i]=v[i]-h*w;
        }
        else if(v[i]<=0){
            v[i]=v[i]+h*w;
        }
    }
    std::sort(v.begin(),v.end());
    for(int i=0;i<h*w-1;++i){
        if(i==h*w-1){
            std::cout<<v[i]<<std::endl;
        }
        else{
            std::cout<<v[i]<<" ";
        }
    }
}
