#include<iostream>
#include<vector>
#include<algorithm>
#include<functional>
#include<map>
#include<string.h>
#include<string>
#include<cmath>
#include<utility>
#define rep(i,rept) for (ll i = 0; i < rept; i++)
typedef long long ll;

int main() {
	int n = 0,num = 0;
	std::cin >> n;
	std::vector<int> kisu, gusu;
	rep(i, n) {
		std::cin >> num;
		if (num % 2 == 0) {
			gusu.push_back(num);
		}
		else {
			kisu.push_back(num);
		}
	}
	std::sort(gusu.begin(),gusu.end());
	std::sort(kisu.begin(), kisu.end());

	rep(i, kisu.size()) {
		std::cout << kisu[i] << " ";
	}
	for(int i = 0; i<gusu.size();i++){
		if (i != gusu.size() - 1) {
			std::cout << gusu[i] << " ";
		}
		else {
			std::cout << gusu[i];
		}
	}
	std::cout << std::endl;

	return 0;
}
